import tkinter as tk
import base64


class ImageButton(tk.Button):

    def __init__(self, parent, type, callback):
        # Must store image ref or image is immediately garbage collected
        self.image_ref = self._get_tkimage(type).zoom(1, 1)
        super().__init__(parent, image=self.image_ref, command=callback)

    @staticmethod
    def _get_tkimage(code):
        """
        Returns 25x25 pixel QR code PhotoImage for use where
        barcode scanner will do text entry of CMD-XX (XX=01-12)

        :param code: "CMD-XX" where XX is 01-12
        :return: 25x25 pixel tkPhotoImage with QR barcode of code text
        """
        button_codes = ('check', 'x')
#        print(code)

        if code not in button_codes:
            raise Exception('Code must be in {}'.format(button_codes))

        button_images = {
            'x': "",
            'check': "",
            }
        data = base64.decodebytes(bytes(button_images[code], encoding='utf8'))
        return tk.PhotoImage(data=data)

